$('.moren').on('click', 'a', function(ev) {
	var ev = ev || window.event;
	var target = ev.target || ev.srcElement;
	var $t = $(target);
	$('.moren a').removeClass('quedin');
	$t.addClass('quedin');
});
$('.bianji').on('click', function(ev) {
	var ev = ev || window.event;
	var target = ev.target || ev.srcElement;
	var $t = $(target);
	console.log($t.closest(".xianqin").css('left'))
	if ($t.closest(".xianqin").css('left') == '0px') {
		$t.closest(".xianqin").css('left', '-1.45rem')
	} else {
		$t.closest(".xianqin").css('left', '0px')
	}

})
$('.shanchu').on('click', function(ev) {
	var ev = ev || window.event;
	var target = ev.target || ev.srcElement;
	var $t = $(target);
	$t.closest(".xianqin").remove()


})
$('.xinzhen').on('click',function(){
$('.g_new').removeClass('invisible')
	$('.g_has').addClass('invisible')
	
})
$('.baocun').on('click',function(){
$('.g_new').addClass('invisible')
	$('.g_has').removeClass('invisible')
	
})